const aiOutput = $json.output;
let suggestions = [];

try {
  let cleanOutput = aiOutput.trim();
  if (cleanOutput.startsWith('```')) {
    cleanOutput = cleanOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '');
  }
  suggestions = JSON.parse(cleanOutput);
  if (!Array.isArray(suggestions)) {
    suggestions = [suggestions];
  }
} catch (error) {
  console.error('Failed to parse AI output as JSON:', error);
  return [{ json: { error: 'Failed to parse AI suggestions', rawOutput: aiOutput } }];
}

return suggestions.map(suggestion => ({
  json: {
    suggestionId: suggestion.suggestionId || '',
    relatedSheet: suggestion.relatedSheet || '',
    entityId: String(suggestion.entityId || ''),
    patientName: suggestion.patientName || '',
    action: suggestion.action || '',
    reason: suggestion.reason || '',
    details: suggestion.details || '',
    critical: String(suggestion.critical || 'False'),
    approved: String(suggestion.approved || 'False'),
    assignedTo: suggestion.assignedTo || '',
    timestamp: suggestion.timestamp || new Date().toISOString().replace('T', ' ').substring(0, 19),
    executed: String(suggestion.executed || 'False'),
    executedAt: suggestion.executedAt || '',
    notes: suggestion.notes || ''
  }
}));

// Get all input items
const allItems = $input.all();

// Return all items as-is without combining
return allItems.map(item => ({ json: item.json }));

// Get all items from the merged data
const allItems = $input.all();

// Organize data by sheet type based on the structure
const doctors = [];
const nurses = [];
const patients = [];
const beds = [];
const riskPatients = [];
const appointments = [];

// Process each item and categorize based on available fields
for (const item of allItems) {
  const data = item.json;

  // Identify data type based on unique fields
  if (data.doctor_id) {
    doctors.push({
      doctor_id: data.doctor_id,
      name: data.name,
      specialty: data.specialty,
      current_load: data.current_load,
      max_load: data.max_load,
      department: data.department,
      status: data.status
    });
  }

  if (data.nurse_id) {
    nurses.push({
      nurse_id: data.nurse_id,
      name: data.name,
      shift: data.shift,
      department: data.department,
      status: data.status,
      current_patients: data.current_patients
    });
  }

  if (data.patient_id) {
    patients.push({
      patient_id: data.patient_id,
      name: data.name,
      age: data.age,
      gender: data.gender,
      risk_level: data.risk_level,
      symptoms: data.symptoms,
      department: data.department,
      status: data.status,
      assigned_bed: data.assigned_bed,
      assigned_doctor: data.assigned_doctor,
      timestamp: data.timestamp
    });
  }

  if (data.bed_id) {
    beds.push({
      bed_id: data.bed_id,
      type: data.type,
      status: data.status,
      patient_id: data.patient_id,
      department: data.department
    });
  }

  if (data.sessionId && data.riskLevel) {
    riskPatients.push({
      sessionId: data.sessionId,
      patientName: data.patientName,
      age: data.age,
      symptoms: data.symptoms,
      riskLevel: data.riskLevel,
      summary: data.summary,
      reason: data.reason,
      department: data.department,
      timestamp: data.timestamp
    });
  }

  if (data['Session ID'] && data['Patient Name']) {
    appointments.push({
      sessionId: data['Session ID'],
      patientName: data['Patient Name'],
      contact: data.Contact,
      doctor: data.Doctor,
      department: data.Department,
      appointmentDate: data['Appointment Date'],
      time: data.Time,
      status: data.Status,
      doctorAvailability: data['Doctor Availability'],
      rescheduledDateTime: data['Rescheduled Date/Time'],
      createdAt: data['Created At'],
      updatedAt: data['Updated At'],
      notes: data.Notes
    });
  }
}

// Create structured text for AI processing
const mergedText = `
Hospital Data Summary:

Doctors (${doctors.length} total):
${JSON.stringify(doctors, null, 2)}

Nurses (${nurses.length} total):
${JSON.stringify(nurses, null, 2)}

Patients (${patients.length} total):
${JSON.stringify(patients, null, 2)}

Beds (${beds.length} total):
${JSON.stringify(beds, null, 2)}

Risk Patients (${riskPatients.length} total):
${JSON.stringify(riskPatients, null, 2)}

Appointments (${appointments.length} total):
${JSON.stringify(appointments, null, 2)}

Please analyze this data and provide:
1. Critical alerts for high-risk situations
2. AI suggestions for optimization
3. Dashboard-ready formatted data
`;

return [{
  json: {
    mergedText: mergedText,
    doctors: doctors,
    nurses: nurses,
    patients: patients,
    beds: beds,
    riskPatients: riskPatients,
    appointments: appointments,
    totalRecords: allItems.length
  }
}];

return items.map(item => {
  return {
    json: {
      suggestionId: item.json.suggestionId,
      status: item.json["Approved/Rejected"]  // Approved / Rejected column
    }
  };
});

